#include <stdio.h>
#include <string.h>
#include <malloc.h>

const char *C = "";

const char *KEY = "BATMAN";

char *solve()
{
    int C_len = strlen(C);
    int key_len = strlen(KEY);
    char *M = (char *)malloc(sizeof(char) * (C_len + 1));
    int i;
    for (i = 0; i < C_len; i++)
    {
        M[i] = (C[i] - KEY[i % key_len] + 26) % 26 + 'A';
    }
    M[i] = '\0';
    return M;
}

int main()
{
    char *M = solve();
    printf("%s\n", M);
    free(M);
    return 0;
}